var request = require("request");
var sanitizeHtml = require('sanitize-html');
var chalk = require('chalk');
module.exports = function (urls, callback) {
    request({
        uri: urls,
    }, function (error, response, body) {
        var removeJavascript = sanitizeHtml(body);
        var test = removeJavascript.replace(/<(?:.|\n)*?>/gm, '');
        var removespacing = test.replace(/\s\s+/g, ' ');
        var removespacing2 = removespacing.replace(/[^\w\s]/gi, '');
        var removespacing3  = removespacing2.replace('\t','');
        callback(removespacing3.split(" "));
    });

}
